st<span class="token operator">=</span><span class="token punctuation">{</span><span class="token number">1</span><span class="token punctuation">,</span><span class="token number">2</span><span class="token punctuation">,</span><span class="token number">3</span><span class="token punctuation">}</span>
<span class="token keyword">print</span><span class="token punctuation">(</span>lst<span class="token punctuation">,</span><span class="token string">"\n"</span><span class="token punctuation">,</span>bta<span class="token punctuation">,</span><span class="token string">"\n"</span><span class="token punctuation">,</span>dct<span class="token punctuation">,</span><span class="token string">"\n"</span><span class="token punctuation">,</span>st<span class="token punctuation">)</span>

